        $maiaProducts[] = array('price' => 18, 'worktopColour' => 'Vulcano', 'url' => 'maia-vulcano', 'productCode' => 'VUL/EDG', 'deliveryBand' => 1, 'header' => '1200mm × 45mm (for 42mm) Edging Strip');
        $maiaProducts[] = array('price' => 20, 'worktopColour' => 'Vulcano', 'url' => 'maia-vulcano', 'productCode' => 'VUL54/EDG', 'deliveryBand' => 1, 'header' => '1200mm × 57mm (for 54mm) Edging Strip');
        $maiaProducts[] = array('price' => 33, 'worktopColour' => 'Vulcano', 'url' => 'maia-vulcano', 'productCode' => 'VUL/JKIT', 'deliveryBand' => 1, 'header' => 'Jointing Kit');
        $maiaProducts[] = array('price' => 20, 'worktopColour' => 'Vulcano', 'url' => 'maia-vulcano', 'productCode' => 'VUL/GLUE', 'deliveryBand' => 1, 'header' => '75ml Adhesive Sachet');
        $maiaProducts[] = array('price' => 45, 'worktopColour' => 'Vulcano', 'url' => 'maia-vulcano', 'productCode' => 'VUL/250GLUE', 'deliveryBand' => 1, 'header' => '50ml Adhesive Cartridge');
        $maiaProducts[] = array('price' => 35, 'worktopColour' => 'Vulcano', 'url' => 'maia-vulcano', 'productCode' => 'VUL/50JKIT', 'deliveryBand' => 1, 'header' => '50ml Cartridge Joint Kit');
        $maiaProducts[] = array('price' => 25, 'worktopColour' => 'Vulcano', 'url' => 'maia-vulcano', 'productCode' => 'VUL/50GLUE', 'deliveryBand' => 1, 'header' => '50ml Adhesive Cartridge');
        $maiaProducts[] = array('price' => 59, 'worktopColour' => 'Vulcano', 'url' => 'maia-vulcano', 'productCode' => 'VUL/CB', 'deliveryBand' => 1, 'header' => 'Chopping Board');

        // Get filename
        $filename = $this->getContainer()->get('kernel')->getRootDir() . '/../' . $input->getArgument('filename');

        $this->getContainer()->enterScope('request');
        $this->getContainer()->set('request', new Request(), 'request');

        /**
         * @var $em EntityManager
         * @var $product Product
         * @var $variant Variant
         */
        $em = $this->getContainer()->get('doctrine')->getManager();
        $productManager = $this->getContainer()->get('kac_site.manager.product');
        $router = $this->getContainer()->get('router');
        $router->getContext()->setHost($input->getArgument('host'));

        // Clear file
        file_put_contents($filename, '');

        // Create XML writer
        $xmlWriter = new \XMLWriter();
        $xmlWriter->openMemory();
        $xmlWriter->setIndent(true);
        $xmlWriter->startDocument('1.0', 'UTF-8');

        $batchSize = 20;
        $i = 0;

        $xmlWriter->startElement('rss');
        $xmlWriter->writeAttribute('xmlns:g', 'http://base.google.com/ns/1.0');
        $xmlWriter->writeAttribute('version', '2.0');

        $xmlWriter->startElement('channel');

        // Add shop info to feed
        $xmlWriter->startElement('title');
        $xmlWriter->writeCdata('Worktops, Charcoal Filters, Cooker Hoods, Built-in Microwaves, Integrated Dishwashers, Integrated Washing Machines, Refrigeration, Cleaning Products, Glass Splashbacks, Waste Disposer, Sinks, Taps, Plinth Heaters, Range Cookers, Ducting, Hobs, Ovens, Coffee Machines, Solid Surface Worktops, Kitchen Appliances');
        $xmlWriter->endElement();

        $xmlWriter->startElement('link');
        $xmlWriter->writeCdata($router->generate('homepage', array(), true));
        $xmlWriter->endElement();

        $xmlWriter->startElement('description');
        $xmlWriter->writeCdata('Kitchen Appliance Centre - A wide selection of kitchen appliances including Kitchen Sinks, Range Cookers, Fridges, Freezers, Dishwashers, Washing Machines, Worktops, Ovens and Hobs, all at low affordable prices.');
        $xmlWriter->endElement();

        // Get the products
        $result = $em->createQuery("SELECT p FROM KAC\\SiteBundle\\Entity\Product p WHERE p.status = 'a'")->iterate();
        while (($row = $result->next()) !== false) {
            $product = $row[0];

            // Check each variant
            foreach ($product->getVariants() as $variant)
            {
                if (!$product->getDescription()->getPageTitle() || !$variant->getDescription()->getPageTitle())
                {
                    $productManager->updateProduct($product);
                    $em->persist($product);
                    $em->flush();
                    break;
                }
            }

            // Write each variant
            foreach ($product->getVariants() as $variant)
            {
                if (!$product->getDescription() || !$variant->getDescription() || !$product->getDepartment())
                {
                    break;
                }

                $url = $variant->getUrl() ? $variant->getUrl() : $product->getUrl();
                if (!$url)
                {
                    break;
                }

                if (!$product->getDescription()->getPageTitle() || !$variant->getDescription()->getPageTitle())
                {
                    $productManager->updateProduct($product);
                }

                // Build item
                $xmlWriter->startElement('item');
                $xmlWriter->writeElement('title', $variant->getDescription()->getPageTitle());
                $xmlWriter->writeElement('link', $router->generate('routing', array('url' => $url), true));
                $xmlWriter->startElement('description');
                $xmlWriter->writeCdata(
                    $variant->getDescription()->getHeader() . ' ' .
                    ($product->getDepartment()->getDepartment()->getParent() ? $product->getDepartment()->getDepartment()->getParent()->getDescription()->getName() : '') . ' ' .
                    $variant->getProductCode() . ' ' .
                    $variant->getDescription()->getMetaDescription()
                );
                $xmlWriter->endElement();
                $xmlWriter->writeElement('g:id', $variant->getId());
                $xmlWriter->writeElement('g:condition', 'new');
                $xmlWriter->writeElement('g:price', number_format($variant->getPrice()->getListPrice(), 2));
                $xmlWriter->writeElement('g:availability', 'in stock');
                if ($variant->getImage())
                {
                    $xmlWriter->writeElement('g:image_link', $this->getContainer()->get('templating.helper.assets')->getUrl($variant->getImage()->getPublicPath()));
                } elseif ($product->getImage()) {
                    $xmlWriter->writeElement('g:image_link', $this->getContainer()->get('templating.helper.assets')->getUrl($product->getImage()->getPublicPath()));
                } else {
                    $xmlWriter->writeElement('g:image_link', $this->getContainer()->get('templating.helper.assets')->getUrl('/uploads/images/product/product/no-image-large.jpg'));
                }
                $xmlWriter->startElement('g:shipping');
                $xmlWriter->writeElement('g:country', 'GB');
                $xmlWriter->writeElement('g:service', 'Standard Delivery');
                $xmlWriter->writeElement('g:price', number_format($variant->getDeliveryCost(), 2));
                $xmlWriter->endElement();
                $xmlWriter->writeElement('g:shipping_weight', $variant->getWeight());
                $xmlWriter->writeElement('g:mpn', $variant->getProductCode());
                $xmlWriter->writeElement('g:brand', $product->getBrand()->getDescription()->getName());
                $xmlWriter->writeElement('g:google_product_category', $product->getDepartment()->getDepartment()->getDescription()->getGoogleDepartment()->getName());
                $xmlWriter->writeElement('g:product_type', $product->getDepartment()->getDepartment()->getDescription()->getName());

                // Adword labels
                // Delivery
                switch ($variant->getDeliveryBand())
                {
                    case 1:
                        $xmlWriter->writeElement('g:adwords_labels', 'small package');
                        break;
                    case 2:
                        $xmlWriter->writeElement('g:adwords_labels', 'medium package');
                        break;
                    case 3:
                    case 4:
                        $xmlWriter->writeElement('g:adwords_labels', 'large package');
                        break;
                    case 5:
                    case 6:
                        $xmlWriter->writeElement('g:adwords_labels', 'extra large package');
                        break;
                }

                if($variant->getDeliveryCost() == 0)
                {
                    $xmlWriter->writeElement('g:adwords_labels', 'free delivery');
                }

                // Departments
                foreach(array_slice($product->getDepartments()->toArray(), 0, 4) as $department)
                {
                    $xmlWriter->writeElement('g:adwords_labels', preg_replace("/[\r\n\t\s]+/s", " ", preg_replace("/[^a-zA-Z0-9\-\/\ ]?/", "", strtolower($department->getDepartment()->getDescription()->getHeader()))));
                }

                // Features
                if ($variant->getFeatures())
                {
                    foreach (array_slice($variant->getFeatures()->toArray(), 0, 4) as $feature)
                    {
                        if ($feature->getFeatureGroup() !== null && $feature->getFeature() !== null && is_object($feature->getFeatureGroup()) && is_object($feature->getFeature()))
                        {
                            $xmlWriter->writeElement('g:adwords_labels', preg_replace("/[\r\n\t\s]+/s", " ", preg_replace("/[^a-zA-Z0-9\-\/\ ]?/", "", strtolower($feature->getFeatureGroup()->getName() . ' ' . $feature->getFeature()->getName()))));
                        }
                    }
                }

                // Price
                if ($variant->getPrice()->getListPrice() > 0 && $variant->getPrice()->getListPrice() <= 50)
                {
                    $xmlWriter->writeElement('g:adwords_labels', 'price 1-50');
                } elseif ($variant->getPrice()->getListPrice() > 50 && $variant->getPrice()->getListPrice() <= 100) {
                    $xmlWriter->writeElement('g:adwords_labels', 'price 51-100');
                } elseif ($variant->getPrice()->getListPrice() > 100 && $variant->getPrice()->getListPrice() <= 200) {
                    $xmlWriter->writeElement('g:adwords_labels', 'price 101-200');
                } elseif ($variant->getPrice()->getListPrice() > 200 && $variant->getPrice()->getListPrice() <= 500) {
                    $xmlWriter->writeElement('g:adwords_labels', 'price 201-500');
                } elseif ($variant->getPrice()->getListPrice() > 500 && $variant->getPrice()->getListPrice() <= 1000) {
                    $xmlWriter->writeElement('g:adwords_labels', 'price 501-1000');
                } elseif ($variant->getPrice()->getListPrice() > 1000) {
                    $xmlWriter->writeElement('g:adwords_labels', 'price 1000+');
                }
                if ($variant->getPrice()->getRecommendedRetailPrice() > $variant->getPrice()->getListPrice())
                {
                    $xmlWriter->writeElement('g:adwords_labels', 'special offer');
                }

                $xmlWriter->endElement();
            }

            if (($i % $batchSize) === 0) {
                file_put_contents($filename, $xmlWriter->flush(true), FILE_APPEND);
            }

            $em->detach($row[0]);
            ++$i;
        }

        // Add maia products
        $maiaProductCount = 10000;
        foreach ($maiaProducts as $maiaProduct)
        {
            $maiaProductCount++;
            $xmlWriter->startElement('item');
            $xmlWriter->writeElement('title', 'maia '.$maiaProduct['worktopColour'].' '.$maiaProduct['productCode'].' '.str_replace('"', '', $maiaProduct['header']));
            $xmlWriter->writeElement('link', $router->generate('routing', array('url' => $maiaProduct['url']), true));
            $xmlWriter->startElement('description');
            $xmlWriter->writeCdata('maia '.$maiaProduct['worktopColour'].' '.$maiaProduct['productCode'].' '.str_replace('"', '', $maiaProduct['header']));
            $xmlWriter->endElement();
            $xmlWriter->writeElement('g:id', $maiaProductCount);
            $xmlWriter->writeElement('g:condition', 'new');
            $xmlWriter->writeElement('g:price', number_format($maiaProduct['price'], 2));
            $xmlWriter->writeElement('g:availability', 'in stock');
            $xmlWriter->writeElement('g:image_link', $this->getContainer()->get('templating.helper.assets')->getUrl('/uploads/demo/'.$maiaProduct['url'].'@2x.jpg'));
            $xmlWriter->startElement('g:shipping');
            $xmlWriter->writeElement('g:country', 'GB');
            $xmlWriter->writeElement('g:service', 'Standard Delivery');
            $xmlWriter->writeElement('g:price', number_format(35, 2));
            $xmlWriter->endElement();
            $xmlWriter->writeElement('g:shipping_weight', number_format(45, 2));
            $xmlWriter->writeElement('g:mpn', $maiaProduct['productCode']);
            $xmlWriter->writeElement('g:brand', 'maia');
            $xmlWriter->writeElement('g:google_product_category', 'Hardware > Worktops');
            $xmlWriter->writeElement('g:product_type', 'Solid Surface Worktop');
            switch ($variant->getDeliveryBand())
            {
                case 1:
                    $xmlWriter->writeElement('g:adwords_labels', 'small package');
                    break;
                case 6:
                    $xmlWriter->writeElement('g:adwords_labels', 'extra large package');
                    break;
            }
            if ($maiaProduct['price'] > 0 && $maiaProduct['price'] <= 50)
            {
                $xmlWriter->writeElement('g:adwords_labels', 'price 1-50');
            } elseif ($maiaProduct['price'] > 50 && $maiaProduct['price'] <= 100) {
                $xmlWriter->writeElement('g:adwords_labels', 'price 51-100');
            } elseif ($maiaProduct['price'] > 100 && $maiaProduct['price'] <= 200) {
                $xmlWriter->writeElement('g:adwords_labels', 'price 101-200');
            } elseif ($maiaProduct['price'] > 200 && $maiaProduct['price'] <= 500) {
                $xmlWriter->writeElement('g:adwords_labels', 'price 201-500');
            } elseif ($maiaProduct['price'] > 500 && $maiaProduct['price'] <= 1000) {
                $xmlWriter->writeElement('g:adwords_labels', 'price 501-1000');
            } elseif ($maiaProduct['price'] > 1000) {
                $xmlWriter->writeElement('g:adwords_labels', 'price 1000+');
            }
            $xmlWriter->endElement();
        }

        $xmlWriter->endElement(); // Close channel
        $xmlWriter->endElement(); // Close rss

        file_put_contents($filename, $xmlWriter->flush(true), FILE_APPEND);
    }
}